    private fun runTestSet(tests: Map<Pair<Int, Int>, DoubleArray>, functions: List<Evaluable>, fast: Boolean = true) {
        for ((varPoints, results) in tests) {
            println("Starting $varPoints")
            val model = Model(listOf(
                    Model.Variable(
                            name = "v1",
                            range = Pair(0.0,1000.0),
                            varPoints = Pair(varPoints.second, varPoints.first),
                            thresholds = listOf(results.first(), results.last()),
                            equation = listOf(Summand(evaluable = functions))
                    )
            ), listOf())
            val abstract = model.computeApproximation(fast)

            assertTrue(results.zip(abstract.variables[0].thresholds).all {
                Math.abs(it.first - it.second) < error
            }, "expected: ${Arrays.toString(results)}, actual ${abstract.variables[0].thresholds}")
        }
    }

    @Test
    fun fastSimpleHillTest() = runTestSet(fastSimpleHillResults, listOf(simpleHill))

