    fun complexTest(expected: Map<Pair<Int, Int>, List<DoubleArray>>, fast: Boolean) {
        for ((varPoints, results) in expected) {
            println("Starting $varPoints")
            val model = Model(listOf(
                    Model.Variable(
                            name = "v1",
                            range = Pair(0.0,1000.0),
                            varPoints = Pair(varPoints.second, varPoints.first),
                            thresholds = listOf(results[0].first(), results[0].last()),
                            equation = listOf(Summand(evaluable = listOf(simpleHill, complexSigmoid.copy(varIndex = 1))))
                    ),
                    Model.Variable(
                            name = "v1",
                            range = Pair(0.0,1000.0),
                            varPoints = Pair(varPoints.second, varPoints.first),
                            thresholds = listOf(results[1].first(), results[1].last()),
                            equation = listOf(Summand(evaluable = listOf(simpleSigmoid, complexHill.copy(varIndex = 1))))
                    )
            ), listOf())
            val abstract = model.computeApproximation(fast)

            assertTrue(results[0].zip(abstract.variables[0].thresholds).all {
                Math.abs(it.first - it.second) < error
            }, "expected: ${Arrays.toString(results[0])}, actual ${abstract.variables[0].thresholds}")

            assertTrue(results[1].zip(abstract.variables[1].thresholds).all {
                Math.abs(it.first - it.second) < error
            }, "expected: ${Arrays.toString(results[1])}, actual ${abstract.variables[0].thresholds}")
        }
    }

    @Test   //10s -> 8s -> 7s
    fun fastComplexTest() = complexTest(fastComplexResults, true)

    @Test   //40s -> 20s
    fun slowComplexTest() = complexTest(slowComplexResults, false)

